import React, { useState, useEffect } from "react";
import { useHistory, useParams, Link, useNavigate } from "react-router-dom";
import "./AddEdit.css"
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AddEdit = () => {

    const [cname, setCname] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    // get navigate function
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        // check if any field is empty and show error
        if (!cname || !email || !phone) {
            toast.error("Please fill all the fields");
            return;
        }

        axios
            .post("http://localhost:5000/api/post", { cname, email, phone })
            .then
            (() => {
                console.log("Axios response: ", Response.data);
                toast.success("Contact added successfully " + Response.data);
                setCname("");
                setEmail("");
                setPhone("");                
            })
            .catch((err) => toast.error(err.message));

            setTimeout(() => {
                navigate.navigate("/"), 500
            });
    }

    return (
        <div style={{ marginTop: "100px" }}>
            <h2>Add Edit</h2>
            <form
                style={{
                    margin: "auto",
                    padding: "15px",
                    maxWidth: "400px",
                    alignContent: "center"
                }}
                onSubmit={handleSubmit}>

                <label htmlFor="cname">Name</label>
                <input type="text"
                    placeholder="Your name ..."
                    value={cname}
                    onChange={(e) => setCname(e.target.value)}
                />

                <label htmlFor="email">Email</label>
                <input type="email"
                    placeholder="Your email ..."
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />

                <label htmlFor="phone">Phone</label>
                <input type="number"
                    placeholder="Your phone ..."
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                />

                <input type="submit" value="Save" />
                <Link to="/">
                    <input type="button" value="Go Back" />
                </Link>
            </form>
        </div>
    );
};

export default AddEdit;
