import React, { useState, useEffect } from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import "./AddEdit.css"
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const initialState = {
    cname: "",
    email: "",
    phone: "",    
}; 

const AddEdit = () => {
 
    //const { state, setState } = useState(initialState);
    const { cname, email, phone } = "";

    const handleSubmit = (e) => {
        e.preventDefault();       
        // check if any field is empty and show error
        if (!cname || !email || !phone) {
            toast.error("Please fill all the fields");
        }
        else
        {
            axios.post("http://localhost:5000/api/post", {
                cname, 
                email, 
                phone}).then(() => {
                    setState({name: "", email: "", phone: ""});
                }).catch((err) => toast.error(err.respose.data.msg));
        }
    }

    /*const handleInputChange = (e) => {
        const { name, value } = e.target;
        setState({ ...state, [name]: value });
    } */

    return (        
        <div style={{ marginTop: "100px" }}>
            <h2>Add Edit</h2>
            <form 
            style={{
                margin: "auto",
                padding: "15px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}
            >
                
                {/* <label htmlFor="cname">Name</label>
                <input type="text" id="cname" name="cname" placeholder="Your name ..." value={cname} onChange={handleInputChange} />

                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="email" placeholder="Your email ..." value={email} onChange={handleInputChange} />

                <label htmlFor="phone">Phone</label>
                <input type="number" id="phone" name="phone" placeholder="Your phone ..." value={phone} onChange={handleInputChange} />

                <input type="submit" value="Save" />
                <Link to="/">
                    <input type="button" value="Go Back" />
                </Link> */}

                <label htmlFor="cname">Name</label>
                <input type="text" id="cname" name="cname" placeholder="Your name ..."  />

                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="email" placeholder="Your email ..."  />

                <label htmlFor="phone">Phone</label>
                <input type="number" id="phone" name="phone" placeholder="Your phone ..."  />

                <input type="submit" value="Save" />
                <Link to="/">
                    <input type="button" value="Go Back" />
                </Link>
            </form>        
        </div>
    );
};

export default AddEdit;
