import React, { useState, useEffect } from "react";
import { useHistory, useParams, Link, useNavigate } from "react-router-dom";
import "./AddEdit.css"
import axios from "axios";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AddEdit = () => {

    const [cname, setCname] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    // get navigate function
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        // check if any field is empty and show error
        toast.info("Name :" + cname + ", Email :" + email + ", Phone :" + phone + ".");
        if (!cname || !email || !phone) {
            toast.error("Please fill all the fields");
            return;
        }

        toast.success("Contact added successfully " + cname + " " + email + " " + phone);

        axios.post("http://localhost:5000/api/post", { cname, email, phone })
            .then(() => {
                toast.success("Contact added successfully " + Response.data);
                navigate("/");
            }).catch((err) => toast.error(err.message));
    }

    return (
        <div style={{ marginTop: "100px" }}>
            <h2>Add Edit</h2>
            <form
                style={{
                    margin: "auto",
                    padding: "15px",
                    maxWidth: "400px",
                    alignContent: "center"
                }}
                onSubmit={handleSubmit}>

                <label htmlFor="cname">Name</label>
                <input type="text"
                 placeholder="Your name ..." 
                 value={cname} 
                 onChange={(e) => setCname(e.target.value)} />

                <label htmlFor="email">Email</label>
                <input type="email" id="email" name="email" placeholder="Your email ..." value={email} onChange={handleInputChange} />

                <label htmlFor="phone">Phone</label>
                <input type="number" id="phone" name="phone" placeholder="Your phone ..." value={phone} onChange={handleInputChange} />

                <input type="submit" value="Save" />
                <Link to="/">
                    <input type="button" value="Go Back" />
                </Link>
            </form>
        </div>
    );
};

export default AddEdit;
