import React, { useState, useEffect } from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import "./AddEdit.css"
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";

const initialState = {
    name: "",
    email: "",
    phone: ""    
}

const AddEdit = () => {
    const {state, setState} = useState(initialState);
    const { name, email, phone } = state;
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post("http://localhost:5000/api/post", state);
    }

    return (
        <div style={{ marginTop: "100px" }}>
            <form style={{
                margin: "auto",
                padding: "15px",
                maxWidth: "400px",
                alignContent: "center"
            }}
            onSubmit={handleSubmit}>
                <label htmlFor="name">Name</label>
                <input type="text" id="name" name="name" placeholder="Your name ..." value={name} onChange={handleInputChange} />

                <label htmlFor="name">Email</label>
                <input type="text" id="email" name="email" placeholder="Your email ..." value={email} onChange={handleInputChange} />

                <label htmlFor="name">Phone</label>
                <input type="number" id="phone" name="phone" placeholder="Your phone ..." value={phone} onChange={handleInputChange} />
            </form>
        </div>
    );
};

export default AddEdit;
